import styled from 'styled-components'

export const ImgProfile = styled.aside`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  img {
    height: 180px;
    margin-bottom: 10px;
    border-radius: 2px 2px solid red;
  }
`
