import styled from 'styled-components'

type BotaoProps = {
  principal: boolean
  fontSize?: string
}

const Botao = styled.button<BotaoProps>`
  background-color: ${(props) => (props.principal ? 'green' : 'blue')};
  margin: 10px;
  font-size: ${(props) => props.fontSize || '20px'};
`
{
  /*Vou criar um butao que vai erdadr as propriedades do butao de cima*/
}

const ButaoPerigo = styled(Botao)`
  background-color: red;
  {/*Posso aplicar a erancao dentro do  meu css*/}
  span{
    text-decoration: line-through;
  }
`

function Teste() {
  return (
    <>
      <Botao fontSize="14px" principal>
        teste
      </Botao>
      <Botao principal={false}>teste</Botao>
      <ButaoPerigo principal>Perigo</ButaoPerigo>
      {/*Transforma uma tag em link usando o as="a" , posso alterar pra qualquer tag*/}
      <ButaoPerigo as="a" principal>
        Perigo
      </ButaoPerigo>
      <span>heranca</span>
    </>
  )
}

export default Teste
