import styled from 'styled-components'

export const ImgProfile = styled.aside`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  img {
    width: 200px;
    height: auto;
    margin-bottom: 10px;
    border-radius: 5px;
  }

  @media (max-width: 768px) {
    img {
      width: 200px;
      height: auto;
      margin-bottom: 10px;
      border-radius: 5px;
    }
  }
`
